const scenes = $('Split Scenes').first().json.choices[0].message.content.scenes
return scenes.map((scene,index) => {
  return {
    scene_no : index + 1,
    character: scene.character,
    enviroment: scene.environment,
    img_prompt : scene.image_prompt
  }
})

parameters.prompt:
=Generate a cinematic illustration in a **strict vertical portrait format (1080x1920)**.
Do not use landscape or square layout.

Scene: {{ $json.img_prompt }}
Characters: {{ $json.character }}
Environment: {{ $json.environment }}
Camera Angle: Centered and front-facing, waist-up or full-body shot.
Framing: Subject must be **fully visible** within the frame with ample vertical space.
Composition: Vertical layout, soft lighting, background elements should be subtle and not overpower the subject.
Style: Magical, dreamy, kid-friendly illustration with consistent art style across scenes.
Output resolution must be 1080x1920. Portrait format only.

parameters.jsCode:
// Use the output from the node named "Get Story"
const story = $json;

// Convert story to pretty JSON
const content = JSON.stringify(story, null, 2);

// Create a buffer from the JSON string
const buffer = Buffer.from(content, 'utf8');

// Return as binary data for writing
return [
  {
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'application/json',
        fileName: `${$input.first().json.choices[0].message.content.title}.json`
      }
    }
  }
];

const content = JSON.stringify($input.first().json.choices[0].message.content.scenes, null, 2);

// Create a buffer from the JSON string
const buffer = Buffer.from(content, 'utf8');

// Return as binary data for writing
return [
  {
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'application/json',
        fileName: `${$input.first().json.choices[0].message.content.title}.json`
      }
    }
  }
];

const items = $input.all();

for (let i = 0; i < items.length; i++) {
  items[i].json.index = i + 1; // Start index from 1
}

return items;

const output = $input.first().raw || $input.first().json;
const lines = output.stdout.split('\n').filter(line => line.trim().startsWith('{'));
return lines.map(line => {
  try {
    return { json: JSON.parse(line.trim()) };
  } catch (e) {
    return { json: { error: "Invalid JSON output" } };
  }
});
